A = (0,0);
B = (0,3);
C = (4,0);
P = (20/23,21/23);

draw(Circle(A,1));
draw(Circle(B,2));
draw(Circle(C,3));
draw(Circle(P,6/23));
draw(A--B--C--cycle);
draw(A--P);
draw(B--P);
draw(C--P);

label("$P$", P, NE, UnFill);

dot("$A$", A, SW);
dot("$B$", B, NW);
dot("$C$", C, SE);
dot(P);
