for (int i=-7; i<3; ++i) {
draw((i,-0.2)--(i,0.2));
draw((-0.2,i)--(0.2,i));
}
draw((-7,1)--(1,-7),linewidth(1));
draw((1,1)--(-7,-7),linewidth(1));
draw((-8,0)--(3,0),Arrow);
draw((0,-8)--(0,3),Arrow);
label("$x$",(3,0),S);
label("$y$",(0,3),E);
label("-6",(-6,0),N);
label("-6",(0,-6),E);
label("$(-3,-3)$",(-3,-3),W);
[/asy]

