pair r = (0, 0);
pair t = dir(-55);
pair s = dir(-55 - 70);
pair p = dir(-55 + 180);
real qr = Sin(70) / Sin(40);

pair q = qr * dir(-55 - 70 + 180);

draw(r--p--q--s--t--r);

