J = (0,8);
K = (15,0);
L = (15,8);
M = (15,4);
draw(J--K--L--J);
draw(rightanglemark(J,L,K,23));
label("$K$",K,SE);
label("$J$",J,NW);
label("$L$",L,NE);
label("$8$",M,E);
